  puts attr
  movie = (Movie.find_by(title: attr[:title], title_image: attr[:title_image],description:  attr[:description]) || Movie.create(title: attr[:title], title_image: attr[:title_image], description:  attr[:description]))
  puts "movie with title #{attr[:title]} was cteated"

  attr[:categories].each do |category|
